mapper.select:
SELECT
-- 级联表的字段 ,beijing.yonghu_id as yonghuId ,beijing.beijing_name as beijingName ,beijing.beijing_photo as beijingPhoto ,beijing.beijing_types as beijingTypes ,beijing.beijing_geshi_types as beijingGeshiTypes ,beijing.beijing_banshi_types as beijingBanshiTypes ,beijing.beijing_file as beijingFile ,beijing.beijing_content as beijingContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.huiyuan_types as huiyuanTypes ,yonghu.yonghu_email as yonghuEmail
FROM beijing_collection a left JOIN beijing beijing ON a.beijing_id = beijing.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.beijing_id = #{params.beijingId}
and a.yonghu_id = #{params.yonghuId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and beijing.beijing_name like CONCAT('%',#{params.beijingName},'%')
and beijing.beijing_types = #{params.beijingTypes}
and beijing.beijing_geshi_types = #{params.beijingGeshiTypes}
and beijing.beijing_banshi_types = #{params.beijingBanshiTypes}
and beijing.beijing_content like CONCAT('%',#{params.beijingContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.huiyuan_types = #{params.huiyuanTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
order by a.${params.orderBy} desc
