mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.huiyuan_types as huiyuanTypes ,yonghu.yonghu_email as yonghuEmail ,yuansu.yonghu_id as yonghuId ,yuansu.yuansu_name as yuansuName ,yuansu.yuansu_photo as yuansuPhoto ,yuansu.yuansu_types as yuansuTypes ,yuansu.yuansu_geshi_types as yuansuGeshiTypes ,yuansu.yuansu_file as yuansuFile ,yuansu.yuansu_content as yuansuContent
FROM yuansu_collection a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN yuansu yuansu ON a.yuansu_id = yuansu.id
and a.id in
#{item}
and a.yuansu_id = #{params.yuansuId}
and a.yonghu_id = #{params.yonghuId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.huiyuan_types = #{params.huiyuanTypes}
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yuansu.yuansu_name like CONCAT('%',#{params.yuansuName},'%')
and yuansu.yuansu_types = #{params.yuansuTypes}
and yuansu.yuansu_geshi_types = #{params.yuansuGeshiTypes}
and yuansu.yuansu_content like CONCAT('%',#{params.yuansuContent},'%')
order by a.${params.orderBy} desc
